INSERT INTO "CONTRACT_MANAGEMENT"."DOCUMENTS" VALUES( 1,'PR-0000105531',1,'Iscrizione alla camera di commercio, industria ed artigianato ', 'Iscrizione alla camera di commercio, industria ed artigianato con oggetto sociale inerente alla tipologia dell’appalto o autocertificazione in sostituzione.', '', 'COM', '');
INSERT INTO "CONTRACT_MANAGEMENT"."DOCUMENTS" VALUES( 2,'PR-0000105531',1,'Documento di regolarità contributiva (DURC)', 'Documento di regolarità contributiva (DURC in originale) in corso di validità (documento del tipo previsto per appalti di lavori pubblici; il documento deve essere rinnovato e riconsegnato ad ogni scadenza, 4 mesi, per tutto il periodo di gestione del contratto); In caso di presenza di lavoratori in somministrazione/distacco l’Appaltatore dovrà fornire il DURC anche per questi lavoratori, facendo da tramite con i relativi datori di lavoro', '', 'COM', '');
INSERT INTO "CONTRACT_MANAGEMENT"."DOCUMENTS" VALUES( 3,'PR-0000105531',1,'Polizza assicurativa', 'Polizza assicurativa, con relativa quietanza di pagamento premio, contro danni a persone e cose di Enel/impresa/terzi, con rinuncia da parte della società assicuratrice al diritto di rivalsa nei confronti di Enel; la polizza dovrà riportare la seguente dichiarazione: “Questa compagnia di Assicurazione fa espressa rinuncia al diritto di rivalsa nei confronti dell’Enel. La presente polizza copre l’assicurato contro tutti i danni alle persone ed alle cose anche di sua proprietà”', '', 'COM', '');
